public class MySwapTwoNumbers {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x =5;
		int y =11;
		System.out.println("x =" +x);
		System.out.println("y =" +y);
		x=x+y;
		y=x-y;
		x=x-y;
		System.out.println("x =" +x);
		System.out.println("y =" +y);
	
	}

}
